    <script>
        <!-- Переключение табов-->
        function showContent(tabNumber) {
            document.querySelectorAll('.tab').forEach(tab => tab.classList.remove('active'));
            document.querySelectorAll('.content').forEach(content => content.classList.remove('active'));
            document.querySelector('.tab:nth-child(' + tabNumber + ')').classList.add('active');
            document.getElementById('content' + tabNumber).classList.add('active');
        }
        <!-- Переключение табов-->

        <!-- Переключение пагинации -->
        document.querySelectorAll('.page-number').forEach(page => {
            page.addEventListener('click', function () {
                document.querySelectorAll('.page-number').forEach(p => p.classList.remove('active'));
                this.classList.add('active');
            });
        });
        <!-- Переключение пагинации -->
    </script>









